INSERT INTO `news` VALUES ('204', '43', '', '关于公司全体党员学习收看十九大开幕会直播的通知', '综合行政部', '2017-10-17', '<p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">中国共产党第十九次全国代表大会将于</span>2017<span style=\"font-family: 宋体;\">年</span>10<span style=\"font-family: 宋体;\">月</span>18<span style=\"font-family: 宋体;\">日上午</span>9:00<span style=\"font-family: 宋体;\">在北京隆重开幕。请公司所以党员</span>10<span style=\"font-family: 宋体;\">月</span>18<span style=\"font-family: 宋体;\">日上午</span>8:50<span style=\"font-family: 宋体;\">分准时到公司会议室集体收看十九大开幕会直播。收看结束后在当天下午</span>13:30<span style=\"font-family: 宋体;\">分前交每位党员交一篇学习感言感悟！</span></span></p><p><br/></p>', '', 'http://dangjian.famishare.me/upload/audio/11.MP3', '20', '1508484235', '2017-10-20 15:23:55', '0', '1', '');
INSERT INTO `news` VALUES ('205', '43', '', '关于公司全体党员学习收看《榜样》专题节目的通知', '综合行政部', '2017-10-09', '<p style=\"text-indent: 2em; text-align: left; line-height: 2em;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">请公司全体党员在</span>10<span style=\"font-family: 宋体;\">月</span>11<span style=\"font-family: 宋体;\">日前学习收看由中央组织部、中央电视台联合录制的专题节目《榜样》，以“喜迎十九大、共圆中国梦、走进学习榜样、凝集复兴力量”为主题的党员教育专题节目。并于</span>10<span style=\"font-family: 宋体;\">月</span>11<span style=\"font-family: 宋体;\">日前将学习体会和观后感交至综合行政部。公司党支部将在本月召开一次座谈会对学习心得和观后感进行交流！</span></span></p><p style=\"text-indent: 2em; text-align: left; line-height: 2em;\"><br/></p><p><br/></p>', '', '', '16', '1508378568', '2017-10-19 10:02:48', '0', '1', '');
