INSERT INTO `news` VALUES ('232', '46', '/upload/news/1710/201710181504068498.jpg', '测试', '123', '2017-10-18', '<p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">故障现象:误检测</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">检修过程:</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（1）调整线圈频率更好的避开同频干扰。</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（2）检查线圈端子是否紧密双绞，提高线圈抗干扰能力。</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（3）检查线圈对面绝缘是否良好，应该用摇表量。线圈经过了地面伸缩缝，必须使用导管保护线圈，否则线圈的绝缘层有可能在伸缩缝的温度变形下破损，失去对大地的绝缘。是不是线圈位置会产生振动，旁边车道大车经过时会引起振动，把灵敏度开关相应调低。并用环氧树脂封闭线圈有裂缝的地方。</span></p><p><span style=\"font-size: 20px;\"><br/></span></p><p><br/></p>', '', '', '1', '1508310270', '2017-10-18 15:04:30', '10', '1', '');
INSERT INTO `news` VALUES ('233', '48', '/upload/news/1710/201710181519064145.jpg', '测试', '123', '123', '<p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">故障现象:误检测</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">检修过程:</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（1）调整线圈频率更好的避开同频干扰。</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（2）检查线圈端子是否紧密双绞，提高线圈抗干扰能力。</span></p><p style=\"white-space: normal; text-indent: 2em; line-height: 2em;\"><span style=\"font-size: 20px;\">（3）检查线圈对面绝缘是否良好，应该用摇表量。线圈经过了地面伸缩缝，必须使用导管保护线圈，否则线圈的绝缘层有可能在伸缩缝的温度变形下破损，失去对大地的绝缘。是不是线圈位置会产生振动，旁边车道大车经过时会引起振动，把灵敏度开关相应调低。并用环氧树脂封闭线圈有裂缝的地方。</span></p><p style=\"white-space: normal;\"><span style=\"font-size: 20px;\"><br/></span></p><p><br/></p>', '', '', '1', '1508311156', '2017-10-18 15:19:16', '0', '1', '');
INSERT INTO `news` VALUES ('234', '44', '/upload/news/1710/201710181558338408.jpg', '锦路公司“四坚持”保障高速公路机电系统正常运行', '工程技术部', '2017-10-18', '<p style=\"text-indent: 2em; text-align: left; line-height: 2em;\"><span style=\"font-size: 20px;\">为保障高速公路机电设备的正常运行，锦路公司通过“四坚持”始终坚守在保畅的第一线。<br/>　　坚持规范化巡检。通过规范化的日巡检、周巡检作业流程，使设备的隐患和异常及时得到发现和解决，提高了设备的完好率。<br/>　　坚持驻点值班制。该司在所维护的梨温、景鹰、德昌等路段成立了八个维护组，通过24小时驻点值班的方式保证了设备出现故障时的响应速度。<br/>　　坚持沟通交流。多形式与业主单位互动，通过OA、QQ群、微信群等交流工具实时和业主进行沟通交流，并定期开展技能大比拼等活动与业主进行互动。<br/>　　坚持队伍建设。通过学习培训、竞赛活动等多元化途径，提高技术员的职业道德和维护水平，从而实现员工队伍整体素质持续有效的提升。<br/>　　据悉，该司为迎接十九大胜利召开，开展了机电设施故障大清除活动。活动期间共处理设备故障49起，并对外场设施稳固性、安全性进行了排查，保证了所辖路段机电设备安全平稳运行。<br/>　　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 2em;\"><span style=\"font-size: 20px;\">&nbsp;</span></p>', '', 'http://dangjian.famishare.me/upload/audio/112.MP3', '46', '1508378206', '2017-10-19 09:56:46', '0', '1', '');
